mapper.select:
    SELECT 
    	DISTINCT ${COLUNM} AS CD_NM


				, USER_ID, BIRTHDAY


    FROM ${TABLE} 
    	WHERE 1=1


			AND AREA_ID = #{AREA_GB}



		AND USER_ID = #{USER_ID}


		AND USER_TYPE = #{USER_TYPE}


		AND REPLACE(AREA_ID, 0, '') LIKE '%'||REPLACE(#{AREA_ID}, 0, '')||'%'

SELECT COUNT(*)
FROM ${TABLE} 
WHERE 1 = 1

	AND ${COLUMN1} = #{DATA1}


	AND ${COLUMN2} = #{DATA2}

    SELECT 
    	DISTINCT ${TEXT} AS TEXT, ${VALUE} AS VALUE
    FROM ${TABLE} 
    	WHERE 1=1

		AND AREA_ID = #{AREA_GB}


		AND TOWN_ID = #{TOWN_ID}


		AND USER_TYPE = #{USER_TYPE} 



			AND CATGR = (SELECT CD FROM TB_CODE_MASTER WHERE CD_ID = #{TYPE})

    SELECT 
    	DISTINCT USER_SEQ, USER_ID, USER_NM, BIRTHDAY 
    FROM TB_USER_INFO 
    	WHERE 1 = 1


			AND AREA_ID = #{AREA_GB}



		AND USER_ID = #{USER_ID}


		AND USER_NM = #{USER_NM}


		AND USER_TYPE = #{USER_TYPE}
