mapper.select:
SELECT * FROM (SELECT ROWNUM RNUM, A.* FROM (SELECT RENT_USE_SEQ ,RENT_SEQ ,VISITER_ID ,VISIT_DT ,DECODE(EDU_YN,'0','false','true') EDU_YN ,DECODE(EQUIP_USE_YN,'0','false','true') EQUIP_USE_YN ,REMARK ,CRE_USR ,CRE_DT ,MOD_USR ,MOD_DT FROM TB_RENT_USE WHERE 1=1 AND RENT_SEQ = #{RENT_SEQ}
ORDER BY RENT_SEQ ) A ) A WHERE 1 = 1
AND A.RNUM > (#{PAGE} - 1) * #{PAGESIZE} AND A.RNUM <= #{PAGE} * #{PAGESIZE}

mapper.update:
    DELETE FROM TB_RENT_USE
	 WHERE RENT_SEQ = #{RENT_SEQ}
	   AND RENT_USE_SEQ = #{RENT_USE_SEQ}
