mapper.select:
SELECT * FROM (SELECT (ROW_NUMBER() OVER()) AS RNUM, A.* FROM (SELECT CD , CATGR , CD_NM , CD_ID , SORT_NO , CASE WHEN USE_YN='1' THEN true ELSE false end USE_YN, REMARK , CRE_USR , CRE_DT , MOD_USR , MOD_DT FROM TB_CODE_MASTER WHERE 1=1
AND CATGR = #{CATGR}
ORDER BY SORT_NO ) A ) A WHERE 1 = 1
AND A.RNUM > (#{PAGE} - 1) * #{PAGESIZE} AND A.RNUM <= #{PAGE} * #{PAGESIZE}

mapper.update:

UPDATE TB_CODE_MASTER
   SET CD_NM = #{CD_NM, jdbcType=VARCHAR}
      ,CD_ID = #{CD_ID, jdbcType=VARCHAR}
      ,SORT_NO = #{SORT_NO, jdbcType=INTEGER}
      ,USE_YN = #{USE_YN, jdbcType=VARCHAR}
      ,REMARK = #{REMARK, jdbcType=VARCHAR}
      ,MOD_USR = #{MOD_USR, jdbcType=VARCHAR}
      ,MOD_DT = SYSDATE
 WHERE CD = #{CD}
   AND CATGR = #{CATGR}

	    DELETE FROM TB_CODE_MASTER
		 WHERE CD = #{CD}
       	   AND CATGR = #{CATGR}
