mapper.select:
SELECT * FROM ( SELECT T.MENU_SQ, M.MENU_NM AS MAIN_NM, T.MENU_NM, T.MENU_ID, CASE WHEN T.USE_YN = '1' THEN true ELSE false end USE_YN FROM TB_MENU_INFO T, TB_MENU_INFO M WHERE T.PARENT_SQ = M.MENU_SQ AND T.USE_YN = '1' AND M.USE_YN = '1' ORDER BY M.MENU_ORDER, T.MENU_ORDER ) A LEFT JOIN ( SELECT AUTH_SQ, USER_NO, USER_TYPE, MENU_SQ, CASE WHEN AUTH_C = '1' THEN true ELSE false end AUTH_C, CASE WHEN AUTH_R = '1' THEN true ELSE false end AUTH_R, CASE WHEN AUTH_U = '1' THEN true ELSE false end AUTH_U, CASE WHEN AUTH_D = '1' THEN true ELSE false end AUTH_D FROM TB_USER_AUTH WHERE 1 = 1
AND USER_TYPE = #{USER_TYPE}
AND USER_NO = #{USER_NO}
) B ON A.MENU_SQ = B.MENU_SQ

mapper.update:
MERGE INTO TB_USER_AUTH T USING DUAL ON ( T.AREA_GB = #{AREA_GB}
AND T.USER_NO = #{USER_NO}
AND T.USER_TYPE = #{USER_TYPE}
AND T.MENU_SQ = #{MENU_SQ} ) WHEN MATCHED THEN UPDATE SET T.AUTH_C = #{AUTH_C , jdbcType=VARCHAR}, T.AUTH_R = #{AUTH_R , jdbcType=VARCHAR}, T.AUTH_U = #{AUTH_U , jdbcType=VARCHAR}, T.AUTH_D = #{AUTH_D , jdbcType=VARCHAR} WHEN NOT MATCHED THEN INSERT ( T.AUTH_SQ, T.AREA_GB,
T.USER_NO,
T.USER_TYPE,
T.MENU_SQ, T.AUTH_C, T.AUTH_R, T.AUTH_U, T.AUTH_D ) VALUES ( #{AUTH_SQ , jdbcType=NUMERIC}, #{AREA_GB , jdbcType=VARCHAR},
#{USER_NO , jdbcType=NUMERIC},
#{USER_TYPE , jdbcType=VARCHAR},
#{MENU_SQ , jdbcType=NUMERIC}, #{AUTH_C , jdbcType=VARCHAR}, #{AUTH_R , jdbcType=VARCHAR}, #{AUTH_U , jdbcType=VARCHAR}, #{AUTH_D , jdbcType=VARCHAR} )
